typedef struct {
  gchar *title;
  gint event_mask;
  gint x, y;
  gint width;
  gint height;
  GdkWindowClass wclass;
  GdkVisual *visual;
  GdkColormap *colormap;
  GdkWindowType window_type;
  GdkCursor *cursor;
  gchar *wmclass_name;
  gchar *wmclass_class;
  gboolean override_redirect;
  GdkWindowTypeHint type_hint;
} GdkWindowAttr;

void                gdk_window_move                     (GdkWindow *window,
                                                         gint x,
                                                         gint y);

void                gdk_window_resize                   (GdkWindow *window,
