typedef struct {
  GList *children;
  gint16 spacing;
  guint homogeneous : 1;
} GtkBox;

typedef struct {
  GtkWidget *widget;
  guint16 padding;
  guint expand : 1;
  guint fill : 1;
  guint pack : 1;
  guint is_secondary : 1;
} GtkBoxChild;
